import Head from "next/head";
import binsData from "../src/bins"
import Bin from "../components/bin"

export default class extends React.Component {
  constructor() {
    super()

    const engCodes = this.engAuthorityCodes()
    const walCodes = this.welshAuthorityCodes()
    const scotAuthorityCodes = this.scotAuthorityCodes()
    const niAuthorityCodes = this.niAuthorityCodes()

    this.state = {
      bins: { ...binsData.bins },
      engAuthorityCodes: engCodes,
      welshAuthorityCodes: walCodes,
      scotAuthorityCodes: scotAuthorityCodes,
      niAuthorityCodes: niAuthorityCodes,
    }
  }

  async engAuthorityCodes() {
    return this.fetchCodes('https://local-authority-sct.register.gov.uk/records.json?page-size=500')
  }

  async welshAuthorityCodes() {
    return this.fetchCodes('https://principal-local-authority.register.gov.uk/records.json?page-size=500')
  }

  async scotAuthorityCodes() {
    return this.fetchCodes('https://local-authority-sct.register.gov.uk/records.json?page-size=500')
  }

  async niAuthorityCodes() {
    return this.fetchCodes('https://local-authority-nir.register.gov.uk/records.json?page-size=500')
  }

  fetchCodes(url) {
    let codes = {}

    fetch(url)
      .then((response) => response.json())
      .then((results) => {
        for (var localAuthorityCode in results) {
          console.log(results[localAuthorityCode])
          codes[localAuthorityCode] = results[localAuthorityCode]['item'][0]['name']
        }
      })

    return codes
  }

  renderBin(key, binData) {
    debugger
    let councilName
    switch (binData.localAuthorityCountry) {
      case "eng":
        councilName = this.state.engAuthorityCodes[binData.councilCode]
      case "sct":
        councilName = this.state.scotAuthorityCodes[binData.councilCode]
      case "wls":
        councilName = this.state.welshAuthorityCodes[binData.councilCode]
      default:
        councilName = "";
    }

    return (
      <Bin
        key={key}
        {...binData}
        councilName={councilName}
      />
    )
  }


  render() {
    return (
      <React.Fragment>
        <Head>
          <meta charset="utf-8" />
          <meta httpEquiv="X-UA-Compatible" content="IE=edge" />
          <meta name="viewport" content="width=device-width, initial-scale=1" />

          <title>#govbins</title>

          <meta property="og:type" content="website" />
          <meta property="og:url" content="http://govbins.uk/" />
          <meta property="og:title" content="#govbins" />
          <meta property="og:description" content="Cataloguing a nation's bins" />
          <meta property="og:image" content="http://govbins.uk/images/preview-card.png" />

          <meta property="twitter:card" content="summary_large_image" />
          <meta property="twitter:url" content="http://govbins.uk/" />
          <meta property="twitter:title" content="#govbins" />
          <meta property="twitter:description" content="Cataloguing a nation's bins" />
          <meta property="twitter:image" content="http://govbins.uk/images/preview-card.png" />

          <link href="https://fonts.googleapis.com/css?family=Rubik" rel="stylesheet" />
        </Head>

        <section className="grid grid-cols-2 lg:grid-cols-4">
          {binsData.bins.map((bin, index) => {
            return this.renderBin(index, bin)
          })}
        </section>
      </React.Fragment>
    )
  }
}
